import React, { Component } from 'react';
import './Account.css';
import Dropdown from 'react-bootstrap/Dropdown';
import logo from './logo.png'; // Import your logo image

class UserProfile extends Component {
  constructor(props) {
    super(props);
    this.state = {
      profilePic: this.props.user.profilePic || 'path_to_dummy_profile_pic.png',
      uploading: false,
    };
  }

  handleProfilePicClick = () => {
    if (!this.state.uploading) {
      this.profilePicInput.click();
    }
  };

  handleProfilePicChange = (e) => {
    const file = e.target.files[0];

    if (file) {
      this.setState({ uploading: true });

      // Simulate image upload (you would typically send the file to your server here)
      setTimeout(() => {
        this.setState({
          profilePic: URL.createObjectURL(file),
          uploading: false,
        });
      }, 2000);
    }
  };

  render() {
    return (
      <div className="user-profile">
        <div
          className={`profile-pic ${this.state.uploading ? 'uploading' : ''}`}
          onClick={this.handleProfilePicClick}
        >
          <img
            src={this.state.profilePic}
            alt="Profile Pic"
          />
          {this.state.uploading && <div className="uploading-overlay">Uploading...</div>}
        </div>
        <p>Name: {this.props.user.name}</p>
        <p>Email: {this.props.user.email}</p>
        <p>Address: {this.props.user.address}</p>
        <p>Phone Number: {this.props.user.phoneNumber}</p>
        <input
          type="file"
          accept="image/*"
          style={{ display: 'none' }}
          ref={(input) => (this.profilePicInput = input)}
          onChange={this.handleProfilePicChange}
        />
      </div>
    );
  }
}

class ChangePassword extends Component {
  render() {
    return (
      <div className="change-password-section">
        <div className="change-password-dropdown">
          <span role="button" className="change-password-toggle">
            üîí Change Password
          </span>
          <div className="dropdown-content">
            <label>
              Current Password:
              <input
                type="password"
                name="currentPassword"
                value={this.props.changePassword.currentPassword}
                onChange={this.props.handlePasswordInputChange}
              />
            </label>
            <label>
              New Password:
              <input
                type="password"
                name="newPassword"
                value={this.props.changePassword.newPassword}
                onChange={this.props.handlePasswordInputChange}
              />
            </label>
            <label>
              Confirm Password:
              <input
                type="password"
                name="confirmPassword"
                value={this.props.changePassword.confirmPassword}
                onChange={this.props.handlePasswordInputChange}
              />
            </label>
            <div className="center-container">
              <button onClick={this.props.handleChangePassword}>Change Password</button>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

class Account extends Component {
  constructor(props) {
    super(props);
    this.state = {
      user: {
        name: 'John Doe',
        email: 'johndoe@example.com',
        address: '123 Main St, City, Country',
        phoneNumber: '123-456-7890',
        profilePic: 'path_to_dummy_profile_pic.png', // Add profilePic field
        // Add more user data
      },
      settings: {
        receiveNotifications: true,
        darkMode: false,
        language: 'English',
        emailPreferences: {
          promotions: true,
          newsUpdates: true,
          productRecommendations: true,
          // Add more email preferences here
        },
      },
      changePassword: {
        currentPassword: '',
        newPassword: '',
        confirmPassword: '',
      },
      isChangePasswordVisible: false,
      paymentInfo: [], // Initialize with an empty array or preloaded payment info
      newPaymentInfo: {
        cardType: '',
        lastFourDigits: '',
      },
    };
  }

  handleNotificationToggle = () => {
    this.setState((prevState) => ({
      settings: {
        ...prevState.settings,
        receiveNotifications: !prevState.settings.receiveNotifications,
      },
    }));
  };

  handleDarkModeToggle = () => {
    this.setState((prevState) => ({
      settings: {
        ...prevState.settings,
        darkMode: !prevState.settings.darkMode,
      },
    }));
  };

  handleLanguageChange = (e) => {
    const newLanguage = e.target.value;
    this.setState((prevState) => ({
      settings: {
        ...prevState.settings,
        language: newLanguage,
      },
    }));
  };

  handleEmailPreferenceChange = (e) => {
    const preference = e.target.name;
    const value = e.target.checked;
    this.setState((prevState) => ({
      settings: {
        ...prevState.settings,
        emailPreferences: {
          ...prevState.settings.emailPreferences,
          [preference]: value,
        },
      },
    }));
  };

  toggleChangePassword = () => {
    this.setState((prevState) => ({
      isChangePasswordVisible: !prevState.isChangePasswordVisible,
    }));
  };

  handlePasswordInputChange = (e) => {
    const field = e.target.name;
    const value = e.target.value;
    this.setState((prevState) => ({
      changePassword: {
        ...prevState.changePassword,
        [field]: value,
      },
    }));
  };

  handleChangePassword = () => {
    // Add logic to handle password change
    const { currentPassword, newPassword, confirmPassword } = this.state.changePassword;
    if (newPassword === confirmPassword) {
      // Update the password
      // You can add your own password change logic here
      console.log('Password changed successfully');
    } else {
      console.error('New password and confirmation do not match');
    }
  };

  render() {
    return (
      <div>
        <header>
          <h1>
            <img src={logo} alt="Panda Shop Logo" width="150px" height="100px" />
            <i>Panda Shop</i>
          </h1>
        </header>

        <nav>
          <a href="/">Home</a>
          <a href="/shop">Shop</a>
          <a href="/categories">Categories</a>
          <a href="Account">Account</a>
          <div className="search-bar">
            <input type="text" placeholder="Search products..." />
            <button type="submit">Search</button>
          </div>
          <a href="/Services">Services</a>
          <a href="/aboutUs">About Us</a>
          <a href="#">Chatbot</a>
          <a className="button" href="/login">Logout</a>
        </nav>

        <div className="account-container">
          <div className="top-right-settings">
            <Dropdown>
              <Dropdown.Toggle variant="light" id="settings-dropdown">
                ‚öôÔ∏è Settings
              </Dropdown.Toggle>

              <Dropdown.Menu>
                <label className="checkbox-label">
                  Receive Notifications:{" "}
                  <input
                    type="checkbox"
                    checked={this.state.settings.receiveNotifications}
                    onChange={this.handleNotificationToggle}
                  />
                </label>
                <label className="checkbox-label">
                  Dark Mode:{" "}
                  <input
                    type="checkbox"
                    checked={this.state.settings.darkMode}
                    onChange={this.handleDarkModeToggle}
                  />
                </label>
                <label className="checkbox-label">
                  Language:{" "}
                  <select
                    value={this.state.settings.language}
                    onChange={this.handleLanguageChange}
                  >
                    <option value="English">English</option>
                    <option value="Spanish">Spanish</option>
                    <option value="French">French</option>
                  </select>
                </label>
              </Dropdown.Menu>
            </Dropdown>
          </div>

          <div className="left-column">
            <UserProfile user={this.state.user} />
            <ChangePassword
              changePassword={this.state.changePassword}
              handlePasswordInputChange={this.handlePasswordInputChange}
              handleChangePassword={this.handleChangePassword}
            />
          </div>
        </div>
        <footer>
          <p>Contact us at support@example.com</p>
          <p>&copy; 2023 Your E-Commerce App</p>
        </footer>
      </div>
    );
  }
}

export default Account;
