import React, { Component } from 'react';
import './Account.css';
import Dropdown from 'react-bootstrap/Dropdown';

// New UserProfile component
class UserProfile extends Component {
  render() {
    return (
      <div className="user-profile">
        <h2>User Profile</h2>
        <p>Name: {this.props.user.name}</p>
        <p>Email: {this.props.user.email}</p>
        <p>Address: {this.props.user.address}</p>
        <p>Phone Number: {this.props.user.phoneNumber}</p>
        {/* Add more user information fields */}
      </div>
    );
  }
}
class ChangePassword extends Component {
  render() {
    return (
      <div className="change-password-section">
        <div className="change-password-dropdown">
          <span role="button" className="change-password-toggle">
            🔒 Change Password
          </span>
          <div className="dropdown-content">
            <label>
              Current Password:
              <input
                type="password"
                name="currentPassword"
                value={this.props.changePassword.currentPassword}
                onChange={this.props.handlePasswordInputChange}
              />
            </label>
            <label>
              New Password:
              <input
                type="password"
                name="newPassword"
                value={this.props.changePassword.newPassword}
                onChange={this.props.handlePasswordInputChange}
              />
            </label>
            <label>
              Confirm Password:
              <input
                type="password"
                name="confirmPassword"
                value={this.props.changePassword.confirmPassword}
                onChange={this.props.handlePasswordInputChange}
              />
            </label>
            <button onClick={this.props.handleChangePassword}>Change Password</button>
          </div>
        </div>
      </div>
    );
  }
}


// New ShippingAddresses component
class ShippingAddresses extends Component {
  render() {
    return (
      <div className="shipping-address">
      <h2>Shipping Addresses</h2>
      <ul>
        {this.props.shippingAddresses.map((address) => (
          <li key={address.id}>
            <div
              onClick={() => this.toggleExpandAddress(address.id)}
              className="address-title"
              
            >
              {address.address}
            </div>
            {this.state.expandedAddressId === address.id && (
              <div className="address-inputs">
                <input
                  type="text"
                  placeholder="City"
                  value={address.city}
                  onChange={(e) => this.props.handleCityChange(address.id, e.target.value)}
                />
                <input
                  type="text"
                  placeholder="Street"
                  value={address.street}
                  onChange={(e) => this.props.handleStreetChange(address.id, e.target.value)}
                />
                <input
                  type="text"
                  placeholder="State"
                  value={address.state}
                  onChange={(e) => this.props.handleStateChange(address.id, e.target.value)}
                />
                <input
                  type="text"
                  placeholder="Country"
                  value={address.country}
                  onChange={(e) => this.props.handleCountryChange(address.id, e.target.value)}
                />
                <input
                  type="text"
                  placeholder="Pincode"
                  value={address.pincode}
                  onChange={(e) => this.props.handlePincodeChange(address.id, e.target.value)}
                />
              </div>
            )}
            <button onClick={() => this.props.deleteShippingAddress(address.id)}>Delete</button>
          </li>
        ))}
      </ul>
      <div>
        <input
          type="text"
          placeholder="Enter a new address"
          value={this.props.newAddress}
          onChange={this.props.handleNewAddressChange}
        />
        <button onClick={this.props.addShippingAddress}>Add Address</button>
      </div>
    </div>
  );
}
}

// New PaymentInformation component
class PaymentInformation extends Component {
  render() {
    return (
      <div className="payment-info">
        <h2>Payment Information</h2>
        {this.props.paymentInfo.map((payment, index) => (
          <div key={index}>
            <p>Card Type: {payment.cardType}</p>
            <p>Last 4 Digits: {payment.lastFourDigits}</p>
            <button onClick={() => this.props.deletePaymentInfo(index)}>Delete</button>
          </div>
        ))}
        <div>
          <input
            type="text"
            name="cardType"
            placeholder="Card Type"
            value={this.props.newPaymentInfo.cardType}
            onChange={this.props.handlePaymentInputChange}
          />
          <input
            type="text"
            name="lastFourDigits"
            placeholder="Last 4 Digits"
            value={this.props.newPaymentInfo.lastFourDigits}
            onChange={this.props.handlePaymentInputChange}
          />
          <button onClick={this.props.addPaymentInfo}>Add Payment Info</button>
        </div>
      </div>
    );
  }
}

class Account extends Component {
  constructor(props) {
    super(props);
    this.state = {
      user: {
        name: 'John Doe',
        email: 'johndoe@example.com',
        address: '123 Main St, City, Country',
        phoneNumber: '123-456-7890',
        // Add more user data
      },
      settings: {
        receiveNotifications: true,
        darkMode: false,
        language: 'English',
        emailPreferences: {
          promotions: true,
          newsUpdates: true,
          productRecommendations: true,
          // Add more email preferences here
        },
      },
      shippingAddresses: [
        {
          id: 1,
          address: '123 Main St, City, Country',
        },
        {
          id: 2,
          address: '456 Elm St, City, Country',
        },
      ],
      newAddress: '',
      changePassword: {
        currentPassword: '',
        newPassword: '',
        confirmPassword: '',
      },
      isChangePasswordVisible: false,
      paymentInfo: [], // Initialize with an empty array or preloaded payment info
      newPaymentInfo: {
        cardType: '',
        lastFourDigits: '',
      },
    };
  }

  handleNotificationToggle = () => {
    this.setState((prevState) => ({
      settings: {
        ...prevState.settings,
        receiveNotifications: !prevState.settings.receiveNotifications,
      },
    }));
  };

  handleDarkModeToggle = () => {
    this.setState((prevState) => ({
      settings: {
        ...prevState.settings,
        darkMode: !prevState.settings.darkMode,
      },
    }));
  };

  handleLanguageChange = (e) => {
    const newLanguage = e.target.value;
    this.setState((prevState) => ({
      settings: {
        ...prevState.settings,
        language: newLanguage,
      },
    }));
  };

  handleEmailPreferenceChange = (e) => {
    const preference = e.target.name;
    const value = e.target.checked;
    this.setState((prevState) => ({
      settings: {
        ...prevState.settings,
        emailPreferences: {
          ...prevState.settings.emailPreferences,
          [preference]: value,
        },
      },
    }));
  };

  toggleChangePassword = () => {
    this.setState((prevState) => ({
      isChangePasswordVisible: !prevState.isChangePasswordVisible,
    }));
  };

  handlePasswordInputChange = (e) => {
    const field = e.target.name;
    const value = e.target.value;
    this.setState((prevState) => ({
      changePassword: {
        ...prevState.changePassword,
        [field]: value,
      },
    }));
  };

  toggleExpandAddress = (addressId) => {
    this.setState({ expandedAddressId: addressId });
  };

  handleChangePassword = () => {
    // Add logic to handle password change
    const { currentPassword, newPassword, confirmPassword } = this.state.changePassword;
    if (newPassword === confirmPassword) {
      // Update the password
      // You can add your own password change logic here
      console.log('Password changed successfully');
    } else {
      console.error('New password and confirmation do not match');
    }
  };

  // Methods for handling shipping addresses
  addShippingAddress = () => {
    const { shippingAddresses, newAddress } = this.state;
    const newId = shippingAddresses.length + 1;
    const newShippingAddress = {
      id: newId,
      address: newAddress,
    };
    this.setState({
      shippingAddresses: [...shippingAddresses, newShippingAddress],
      newAddress: '', // Clear the input field
    });
  };

  deleteShippingAddress = (id) => {
    const { shippingAddresses } = this.state;
    const updatedShippingAddresses = shippingAddresses.filter(
      (address) => address.id !== id
    );
    this.setState({ shippingAddresses: updatedShippingAddresses });
  };

  handleNewAddressChange = (e) => {
    this.setState({ newAddress: e.target.value });
  };

  // Methods for handling payment info
  handlePaymentInputChange = (e) => {
    const field = e.target.name;
    const value = e.target.value;
    this.setState((prevState) => ({
      newPaymentInfo: {
        ...prevState.newPaymentInfo,
        [field]: value,
      },
    }));
  };

  addPaymentInfo = () => {
    const { paymentInfo, newPaymentInfo } = this.state;
    const updatedPaymentInfo = [...paymentInfo, newPaymentInfo];
    this.setState({
      paymentInfo: updatedPaymentInfo,
      newPaymentInfo: {
        cardType: '',
        lastFourDigits: '',
      },
    });
  };

  deletePaymentInfo = (index) => {
    const { paymentInfo } = this.state;
    const updatedPaymentInfo = paymentInfo.filter((_, i) => i !== index);
    this.setState({ paymentInfo: updatedPaymentInfo });
  };

  render() {
    return (
      <div className="account-container">
        <div className="top-right-settings">
          <Dropdown>
            <Dropdown.Toggle variant="light" id="settings-dropdown">
              ⚙️ Settings
            </Dropdown.Toggle>

            <Dropdown.Menu>
              <label className="checkbox-label">
                Receive Notifications:{" "}
                <input
                  type="checkbox"
                  checked={this.state.settings.receiveNotifications}
                  onChange={this.handleNotificationToggle}
                />
              </label>
              <label className="checkbox-label">
                Dark Mode:{" "}
                <input
                  type="checkbox"
                  checked={this.state.settings.darkMode}
                  onChange={this.handleDarkModeToggle}
                />
              </label>
              <label className="checkbox-label">
                Language:{" "}
                <select
                  value={this.state.settings.language}
                  onChange={this.handleLanguageChange}
                >
                  <option value="English">English</option>
                  <option value="Spanish">Spanish</option>
                  <option value="French">French</option>
                </select>
              </label>
            </Dropdown.Menu>
          </Dropdown>
        </div>

        <div className="left-column">
          <UserProfile user={this.state.user} />
          <ChangePassword
            changePassword={this.state.changePassword}
            handlePasswordInputChange={this.handlePasswordInputChange}
            handleChangePassword={this.handleChangePassword}
          />
        </div>

        <div className="right-column">
          <ShippingAddresses
            shippingAddresses={this.state.shippingAddresses}
            newAddress={this.state.newAddress}
            handleNewAddressChange={this.handleNewAddressChange}
            addShippingAddress={this.addShippingAddress}
            deleteShippingAddress={this.deleteShippingAddress}
          />
          <PaymentInformation
            paymentInfo={this.state.paymentInfo}
            newPaymentInfo={this.state.newPaymentInfo}
            handlePaymentInputChange={this.handlePaymentInputChange}
            addPaymentInfo={this.addPaymentInfo}
            deletePaymentInfo={this.deletePaymentInfo}
          />
        </div>
      </div>
    );
  }
}

export default Account;
