          // Add more email preferences here
        },
      },
      combinedInfo: {
        shippingAddresses: [
          {
            id: 1,
            address: '123 Main St, City, Country',
          },
          {
            id: 2,
            address: '456 Elm St, City, Country',
          },
        ],
        newAddress: '',
        paymentInfo: [], // Initialize with an empty array or preloaded payment info
        newPaymentInfo: {
          cardType: '',
          lastFourDigits: '',
        },
      },
      changePassword: {
        currentPassword: '',
        newPassword: '',
        confirmPassword: '',
      },
      isChangePasswordVisible: false,
    };
  }

  // Methods for handling user settings
  // ... (existing methods)

  render() {
    return (
      <div className="account-container">
        <div className="top-right-settings">
          <Dropdown>
            <Dropdown.Toggle variant="light" id="settings-dropdown">
              ‚öôÔ∏è Settings
            </Dropdown.Toggle>

            <Dropdown.Menu>
              <label className="checkbox-label">
                Receive Notifications:{" "}
                <input
                  type="checkbox"
                  checked={this.state.settings.receiveNotifications}
                  onChange={this.handleNotificationToggle}
                />
              </label>
              <label className="checkbox-label">
                Dark Mode:{" "}
                <input
                  type="checkbox"
                  checked={this.state.settings.darkMode}
                  onChange={this.handleDarkModeToggle}
                />
              </label>
              <label className="checkbox-label">
                Language:{" "}
                <select
                  value={this.state.settings.language}
                  onChange={this.handleLanguageChange}
                >
                  <option value="English">English</option>
                  <option value="Spanish">Spanish</option>
                  <option value="French">French</option>
                </select>
              </label>
            </Dropdown.Menu>
          </Dropdown>
        </div>

        <div className="left-column">
          <UserProfile user={this.state.user} />
          <ChangePassword
            changePassword={this.state.changePassword}
            handlePasswordInputChange={this.handlePasswordInputChange}
            handleChangePassword={this.handleChangePassword}
          />
        </div>

        <div className="right-column">
          <div className="combined-section">
            <h2>Combined Information</h2>
            <ul>
              {this.state.combinedInfo.shippingAddresses.map((address) => (
                <li key={address.id}>
                  {address.address}
                  <button onClick={() => this.deleteShippingAddress(address.id)}>
                    Delete
                  </button>
                </li>
              ))}
            </ul>
            <div>
              <input
                type="text"
                placeholder="Enter a new address"
                value={this.state.combinedInfo.newAddress}
                onChange={this.handleNewAddressChange}
              />
              <button onClick={this.addShippingAddress}>Add Address</button>
            </div>
            {this.state.combinedInfo.paymentInfo.map((payment, index) => (
              <div key={index}>
                <p>Card Type: {payment.cardType}</p>
                <p>Last 4 Digits: {payment.lastFourDigits}</p>
                <button onClick={() => this.deletePaymentInfo(index)}>Delete</button>
              </div>
            ))}
            <div>
              <input
                type="text"
                name="cardType"
                placeholder="Card Type"
                value={this.state.combinedInfo.newPaymentInfo.cardType}
                onChange={this.handlePaymentInputChange}
              />
              <input
                type="text"
                name="lastFourDigits"
                placeholder="Last 4 Digits"
                value={this.state.combinedInfo.newPaymentInfo.lastFourDigits}
                onChange={this.handlePaymentInputChange}
              />
              <button onClick={this.addPaymentInfo}>Add Payment Info</button>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Account;
