import React, { Component } from 'react';
import './Account.css'; // Import your CSS file
class Account extends Component {
  constructor(props) {
    super(props);
    this.state = {
      user: {
        name: 'John Doe',
        email: 'johndoe@example.com',
        // Other user data
      },
      settings: {
        receiveNotifications: true,
        darkMode: false,
        language: 'English',
        emailPreferences: {
          promotions: true,
          newsUpdates: true,
          productRecommendations: true,
          // Add more email preferences here
        },
        // Add other user settings
      },
    };
  }

  // Methods for handling user settings
  handleNotificationToggle = () => {
    this.setState((prevState) => ({
      settings: {
        ...prevState.settings,
        receiveNotifications: !prevState.settings.receiveNotifications,
      },
    }));
  };

  handleDarkModeToggle = () => {
    this.setState((prevState) => ({
      settings: {
        ...prevState.settings,
        darkMode: !prevState.settings.darkMode,
      },
    }));
  };

  handleLanguageChange = (e) => {
    const newLanguage = e.target.value;
    this.setState((prevState) => ({
      settings: {
        ...prevState.settings,
        language: newLanguage,
      },
    }));
  };

  handleEmailPreferenceChange = (e) => {
    const preference = e.target.name;
    const value = e.target.checked;
    this.setState((prevState) => ({
      settings: {
        ...prevState.settings,
        emailPreferences: {
          ...prevState.settings.emailPreferences,
          [preference]: value,
        },
      },
    }));
  };

  render() {
    return (
      <div className="account-container">
        <h1>Account Information</h1>
        <div className="user-profile">
          <h2>User Profile</h2>
          <p>Name: {this.state.user.name}</p>
          <p>Email: {this.state.user.email}</p>
          {/* Add forms and UI elements for editing user information */}
        </div>
        <div className="user-settings">
          <h2>User Settings</h2>
          <label className="checkbox-label">
            Receive Notifications:
            <input
              type="checkbox"
              checked={this.state.settings.receiveNotifications}
              onChange={this.handleNotificationToggle}
            />
          </label>
          <br />
          <label className="checkbox-label">
            Dark Mode:
            <input
              type="checkbox"
              checked={this.state.settings.darkMode}
              onChange={this.handleDarkModeToggle}
            />
          </label>
          <br />
          <label className="checkbox-label">
            Language:
            <select className="select" value={this.state.settings.language} onChange={this.handleLanguageChange}>
              <option value="English">English</option>
              <option value="Spanish">Spanish</option>
              {/* Add more language options */}
            </select>
          </label>
          <br />
          <label className="checkbox-label">
            Email Preferences:
            <div>
              <label className="checkbox-label">
                Promotions:
                <input
                  type="checkbox"
                  name="promotions"
                  checked={this.state.settings.emailPreferences.promotions}
                  onChange={this.handleEmailPreferenceChange}
                />
              </label>
              <label className="checkbox-label">
                News Updates:
                <input
                  type="checkbox"
                  name="newsUpdates"
                  checked={this.state.settings.emailPreferences.newsUpdates}
                  onChange={this.handleEmailPreferenceChange}
                />
              </label>
              <label className="checkbox-label">
                Product Recommendations:
                <input
                  type="checkbox"
                  name="productRecommendations"
                  checked={this.state.settings.emailPreferences.productRecommendations}
                  onChange={this.handleEmailPreferenceChange}
                />
              </label>
              {/* Add more email preference options */}
            </div>
          </label>
          {/* Add more user settings options */}
        </div>
        {/* Add other sections like order history, wish lists, etc. */}
        <footer>
        <p color="black">Contact us at support@example.com</p>
        <p>&copy; 2023 Panda Shop App</p>
      </footer>
      </div>
      
    );
  }
}

export default Account;
