import React, { Component } from 'react';
import './Account.css';
import logo from './logo.png';
import Dropdown from 'react-bootstrap/Dropdown';

class Account extends Component {
  constructor(props) {
    super(props);
    this.state = {
      user: {
        name: 'John Doe',
        email: 'johndoe@example.com',
        address: '123 Main St, City, Country',
        phoneNumber: '123-456-7890',
        // Add more user data
      },
      settings: {
        receiveNotifications: true,
        darkMode: false,
        language: 'English',
        emailPreferences: {
          promotions: true,
          newsUpdates: true,
          productRecommendations: true,
          // Add more email preferences here
        },
      },
      shippingAddresses: [
        {
          id: 1,
          address: '123 Main St, City, Country',
        },
        {
          id: 2,
          address: '456 Elm St, City, Country',
        },
      ],
      newAddress: '',
      changePassword: {
        currentPassword: '',
        newPassword: '',
        confirmPassword: '',
      },
      isChangePasswordVisible: false,
      paymentInfo: [], // Initialize with an empty array or preloaded payment info
      newPaymentInfo: {
        cardType: '',
        lastFourDigits: '',
      },
    };
  }

  // Methods for handling user settings
  handleNotificationToggle = () => {
    this.setState((prevState) => ({
      settings: {
        ...prevState.settings,
        receiveNotifications: !prevState.settings.receiveNotifications,
      },
    }));
  };

  handleDarkModeToggle = () => {
    this.setState((prevState) => ({
      settings: {
        ...prevState.settings,
        darkMode: !prevState.settings.darkMode,
      },
    }));
  };

  handleLanguageChange = (e) => {
    const newLanguage = e.target.value;
    this.setState((prevState) => ({
      settings: {
        ...prevState.settings,
        language: newLanguage,
      },
    }));
  };

  handleEmailPreferenceChange = (e) => {
    const preference = e.target.name;
    const value = e.target.checked;
    this.setState((prevState) => ({
      settings: {
        ...prevState.settings,
        emailPreferences: {
          ...prevState.settings.emailPreferences,
          [preference]: value,
        },
      },
    }));
  };

  toggleChangePassword = () => {
    this.setState((prevState) => ({
      isChangePasswordVisible: !prevState.isChangePasswordVisible,
    }));
  };

  handlePasswordInputChange = (e) => {
    const field = e.target.name;
    const value = e.target.value;
    this.setState((prevState) => ({
      changePassword: {
        ...prevState.changePassword,
        [field]: value,
      },
    }));
  };

  handleChangePassword = () => {
    // Add logic to handle password change
    const { currentPassword, newPassword, confirmPassword } = this.state.changePassword;
    if (newPassword === confirmPassword) {
      // Update the password
      // You can add your own password change logic here
      console.log('Password changed successfully');
    } else {
      console.error('New password and confirmation do not match');
    }
  };

  // Methods for handling shipping addresses
  addShippingAddress = () => {
    const { shippingAddresses, newAddress } = this.state;
    const newId = shippingAddresses.length + 1;
    const newShippingAddress = {
      id: newId,
      address: newAddress,
    };
    this.setState({
      shippingAddresses: [...shippingAddresses, newShippingAddress],
      newAddress: '', // Clear the input field
    });
  };

  deleteShippingAddress = (id) => {
    const { shippingAddresses } = this.state;
    const updatedShippingAddresses = shippingAddresses.filter(
      (address) => address.id !== id
    );
    this.setState({ shippingAddresses: updatedShippingAddresses });
  };

  handleNewAddressChange = (e) => {
    this.setState({ newAddress: e.target.value });
  };

  // Methods for handling payment info
  handlePaymentInputChange = (e) => {
    const field = e.target.name;
    const value = e.target.value;
    this.setState((prevState) => ({
      newPaymentInfo: {
        ...prevState.newPaymentInfo,
        [field]: value,
      },
    }));
  };

  addPaymentInfo = () => {
    const { paymentInfo, newPaymentInfo } = this.state;
    const updatedPaymentInfo = [...paymentInfo, newPaymentInfo];
    this.setState({
      paymentInfo: updatedPaymentInfo,
      newPaymentInfo: {
        cardType: '',
        lastFourDigits: '',
      },
    });
  };

  deletePaymentInfo = (index) => {
    const { paymentInfo } = this.state;
    const updatedPaymentInfo = paymentInfo.filter((_, i) => i !== index);
    this.setState({ paymentInfo: updatedPaymentInfo });
  };

  render() {
    return (
      <div className="account-container">
        <header>
          <h1>
            <img src={logo} alt="" width="150px" height="100px" />
            <i>Panda Shop</i>
          </h1>
        </header>

        <nav>
          {/* ... Navigation links */}
        </nav>

        <div className="page-content">
          <div className="left-column">
            {/* User Profile Section */}
            <h2>User Profile</h2>
            <p>Name: {this.state.user.name}</p>
            <p>Email: {this.state.user.email}</p>
            <p>Address: {this.state.user.address}</p>
            <p>Phone Number: {this.state.user.phoneNumber}</p>

            {/* Change Password Section */}
            <div className="change-password-section">
              <Dropdown>
                <Dropdown.Toggle variant="light" id="password-dropdown">
                  ðŸ”’ Change Password
                </Dropdown.Toggle>

                <Dropdown.Menu style={{ backgroundColor: 'black' }}>
                  <label>
                    Current Password:
                    <input
                      type="password"
                      name="currentPassword"
                      value={this.state.changePassword.currentPassword}
                      onChange={this.handlePasswordInputChange}
                    />
                  </label>
                  <label>
                    New Password:
                    <input
                      type="password"
                      name="newPassword"
                      value={this.state.changePassword.newPassword}
                      onChange={this.handlePasswordInputChange}
                    />
                  </label>
                  <label>
                    Confirm Password:
                    <input
                      type="password"
                      name="confirmPassword"
                      value={this.state.changePassword.confirmPassword}
                      onChange={this.handlePasswordInputChange}
                    />
                  </label>
                  <button onClick={this.handleChangePassword}>Change Password</button>
                </Dropdown.Menu>
              </Dropdown>
            </div>
          </div>

          <div className="right-column">
            {/* Shipping Addresses Section */}
            <h2>Shipping Addresses</h2>
            {this.state.shippingAddresses.map((address) => (
              <div key={address.id}>
                {address.address}
                <button onClick={() => this.deleteShippingAddress(address.id)}>
                  Delete
                </button>
              </div>
            ))}
            <div>
              <input
                type="text"
                placeholder="Enter a new address"
                value={this.state.newAddress}
                onChange={this.handleNewAddressChange}
              />
              <button onClick={this.addShippingAddress}>Add Address</button>
            </div>

            {/* Payment Information Section */}
            <h2>Payment Information</h2>
            {this.state.paymentInfo.map((payment, index) => (
              <div key={index}>
                <p>Card Type: {payment.cardType}</p>
                <p>Last 4 Digits: {payment.lastFourDigits}</p>
                <button onClick={() => this.deletePaymentInfo(index)}>Delete</button>
              </div>
            ))}
            <div>
              <input
                type="text"
                name="cardType"
                placeholder="Card Type"
                value={this.state.newPaymentInfo.cardType}
                onChange={this.handlePaymentInputChange}
              />
              <input
                type="text"
                name="lastFourDigits"
                placeholder="Last 4 Digits"
                value={this.state.newPaymentInfo.lastFourDigits}
                onChange={this.handlePaymentInputChange}
              />
              <button onClick={this.addPaymentInfo}>Add Payment Info</button>
            </div>
          </div>
        </div>

        <footer>
          <p>Contact us at support@example.com</p>
          <p>&copy; 2023 Your E-Commerce App</p>
        </footer>
      </div>
    );
  }
}

export default Account;
