import React, { useState } from "react";
import { ToastContainer } from "react-toastify";
import axios from "axios";
import add from './add.jpg';
import view from "./view.jpg";
import logo from './logo.png';

const Services = () => {
  const [inputValue, setInputValue] = useState({
    item_name: "",
    item_type: "",
    description: "",
    price: "",
    size: "",
    manufacturer: "",
    item_image: "",
  });
  const { item_name, item_type, description, price, size, manufacturer, item_image } = inputValue;

  const handleOnChange = (e) => {
    const { id, value } = e.target;
    setInputValue({
      ...inputValue,
      [id]: value,
    });
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setInputValue({
          ...inputValue,
          item_image: reader.result,
        });
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const { data } = await axios.post(
        "http://localhost:4000/additem",
        {
          ...inputValue,
        },
        { withCredentials: true }
      );
      const { success, message } = data;
      if (success) {
        alert("Item Added Successfully!!!");
      } else {
        alert(message);
      }
    } catch (error) {
      console.log(error);
    }
    setInputValue({
      item_name: "",
      item_type: "",
      description: "",
      price: "",
      size: "",
      manufacturer: "",
      item_image: "",
    });
  };

  return (
    <div className="services-container">
      <header>
        <img src={logo} alt="" width="150px" height="100px" />
        <i>Panda Shop</i>
      </header>
      <nav>
        <ul>
          <li><a href="/">Home</a></li>
          <li><a href="/shop">Shop</a></li>
          <li><a href="/categories">Categories</a></li>
          <li><a href="#">Account</a></li>
          <li>
            <div className="search-bar">
              <input type="text" placeholder="Search products..." />
              <button type="submit">Search</button>
            </div>
          </li>
          <li><a href="/Services">Services</a></li>
          <li><a href="/aboutUs">About Us</a></li>
          <li><a href="#">Chatbot</a></li>
          <li><a className="button" href="/login">Logout</a></li>
        </ul>
      </nav>

      <div className="welcome-section">
        <h1>Welcome</h1>
        <p>You can sell your products here!</p>
      </div>

      <div className="product-listing">
        <div className="add-item">
          <img src={add} alt="" />
          <h2>Add Item</h2>
          <button
            type="button"
            className="btn btn-success"
            data-bs-toggle="modal"
            data-bs-target="#exampleModal"
          >
            Add
          </button>
        </div>
        <div className="view-item">
          <img src={view} alt="" />
          <h2>View Item</h2>
          <a
            href="/view"
            className="btn btn-success"
            style={{ backgroundColor: "#FFA500" }}
          >
            View
          </a>
        </div>
      </div>

      <div className="modal fade" id="exampleModal" tabIndex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
        <div className="modal-dialog">
          <div className="modal-content">
            <div className="modal-header">
              <h1 className="modal-title fs-5" id="exampleModalLabel">
                Add Items to the App
              </h1>
              <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
            </div>
            <div className="modal-body">
              <form onSubmit={handleSubmit}>
                {/* Your form fields go here */}
                <button type="button" className="btn" data-bs-dismiss="modal">Close</button>
                <button type="submit" className="btn">Add Item</button>
              </form>
            </div>
          </div>
        </div>
      </div>

      <ToastContainer />
    </div>
  );
};

export default Services;
