import React, { Component } from 'react';
import './Account.css';
import Dropdown from 'react-bootstrap/Dropdown';

// New UserProfile component
class UserProfile extends Component {
  render() {
    return (
      <div className="account-container">
        <div className="top-right-settings">
          <Dropdown>
            <Dropdown.Toggle variant="light" id="settings-dropdown">
              ⚙️ Settings
            </Dropdown.Toggle>

            <Dropdown.Menu>
              <label className="checkbox-label">
                Receive Notifications:{" "}
                <input
                  type="checkbox"
                  checked={this.props.settings.receiveNotifications}
                  onChange={this.props.handleNotificationToggle}
                />
              </label>
              <label className="checkbox-label">
                Dark Mode:{" "}
                <input
                  type="checkbox"
                  checked={this.props.settings.darkMode}
                  onChange={this.props.handleDarkModeToggle}
                />
              </label>
              <label className="checkbox-label">
                Language:{" "}
                <select
                  value={this.props.settings.language}
                  onChange={this.props.handleLanguageChange}
                >
                  <option value="English">English</option>
                  <option value="Spanish">Spanish</option>
                  <option value="French">French</option>
                </select>
              </label>
            </Dropdown.Menu>
          </Dropdown>
        </div>
        <div className="user-profile">
          <h2>User Profile</h2>
          <p>Name: {this.props.user.name}</p>
          <p>Email: {this.props.user.email}</p>
          <p>Address: {this.props.user.address}</p>
          <p>Phone Number: {this.props.user.phoneNumber}</p>
          {/* Add more user information fields */}
        </div>
      </div>
    );
  }
}

class Account extends Component {
  constructor(props) {
    super(props);
    this.state = {
      user: {
        name: 'John Doe',
        email: 'johndoe@example.com',
        address: '123 Main St, City, Country',
        phoneNumber: '123-456-7890',
        // Add more user data
      },
      settings: {
        receiveNotifications: true,
        darkMode: false,
        language: 'English',
        emailPreferences: {
          promotions: true,
          newsUpdates: true,
          productRecommendations: true,
          // Add more email preferences here
        },
      },
    };
  }

  handleNotificationToggle = () => {
    this.setState((prevState) => ({
      settings: {
        ...prevState.settings,
        receiveNotifications: !prevState.settings.receiveNotifications,
      },
    }));
  };

  handleDarkModeToggle = () => {
    this.setState((prevState) => ({
      settings: {
        ...prevState.settings,
        darkMode: !prevState.settings.darkMode,
      },
    }));
  };

  handleLanguageChange = (e) => {
    const newLanguage = e.target.value;
    this.setState((prevState) => ({
      settings: {
        ...prevState.settings,
        language: newLanguage,
      },
    }));
  };

  render() {
    return (
      <UserProfile
        user={this.state.user}
        settings={this.state.settings}
        handleNotificationToggle={this.handleNotificationToggle}
        handleDarkModeToggle={this.handleDarkModeToggle}
        handleLanguageChange={this.handleLanguageChange}
      />
    );
  }
}

export default Account;
