import React, { Component } from 'react';
import './Account.css';
import logo from './logo.png';
import Dropdown from 'react-bootstrap/Dropdown';

class Account extends Component {
  constructor(props) {
    super(props);
    this.state = {
      user: {
        name: 'John Doe',
        email: 'johndoe@example.com',
        // Other user data
      },
      settings: {
        receiveNotifications: true,
        darkMode: false,
        language: 'English',
        emailPreferences: {
          promotions: true,
          newsUpdates: true,
          productRecommendations: true,
          // Add more email preferences here
        },
        // Add other user settings
      },
    };
  }

  // Methods for handling user settings
  handleNotificationToggle = () => {
    this.setState((prevState) => ({
      settings: {
        ...prevState.settings,
        receiveNotifications: !prevState.settings.receiveNotifications,
      },
    }));
  };

  handleDarkModeToggle = () => {
    this.setState((prevState) => ({
      settings: {
        ...prevState.settings,
        darkMode: !prevState.settings.darkMode,
      },
    }));
  };

  handleLanguageChange = (e) => {
    const newLanguage = e.target.value;
    this.setState((prevState) => ({
      settings: {
        ...prevState.settings,
        language: newLanguage,
      },
    }));
  };

  render() {
    return (
      <div className="account-container">
        <header>
          <h1>
            <img src={logo} alt="" width="150px" height="100px" />
            <i>Panda Shop</i>
          </h1>
        </header>

        <nav>
          <a href="/">Home</a>
          <a href="/shop">Shop</a>
          <a href="/categories">Categories</a>
          <a href="Account">Account</a>
          <div className="search-bar">
            <input type="text" placeholder="Search products..." />
            <button type="submit">Search</button>
          </div>
          <a href="/Services">Services</a>
          <a href="/aboutUs">About Us</a>
          <a href="#">Chatbot</a>
          <a className="button" href="/login">
            Logout
          </a>
        </nav>

        <div className="user-profile">
          <h2>User Profile</h2>
          <p>Name: {this.state.user.name}</p>
          <p>Email: {this.state.user.email}</p>
          {/* Add forms and UI elements for editing user information */}
        </div>

        <div className="settings-icon">
          <Dropdown>
            <Dropdown.Toggle variant="light" id="dropdown-basic">
              ⚙️
            </Dropdown.Toggle>

            <Dropdown.Menu>
              <label className="checkbox-label">
                Receive Notifications:{" "}
                <input
                  type="checkbox"
                  checked={this.state.settings.receiveNotifications}
                  onChange={this.handleNotificationToggle}
                />
              </label>

              <label className="checkbox-label">
                Dark Mode:{" "}
                <input
                  type="checkbox"
                  checked={this.state.settings.darkMode}
                  onChange={this.handleDarkModeToggle}
                />
              </label>

              <label className="checkbox-label">
                Language:{" "}
                <select
                  value={this.state.settings.language}
                  onChange={this.handleLanguageChange}
                >
                  <option value="English">English</option>
                  <option value="Spanish">Spanish</option>
                </select>
              </label>
            </Dropdown.Menu>
          </Dropdown>
        </div>

        <footer>
          <p>Contact us at support@example.com</p>
          <p>&copy; 2023 Your E-Commerce App</p>
        </footer>
      </div>
    );
  }
}

export default Account;
