import React, { Component } from 'react';
import './Account.css';
import logo from './logo.png';
import Dropdown from 'react-bootstrap/Dropdown';

class Account extends Component {
  constructor(props) {
    super(props);
    this.state = {
      user: {
        name: 'John Doe',
        email: 'johndoe@example.com',
        address: '123 Main St, City, Country',
        phoneNumber: '123-456-7890',
        // Add more user data
      },
      settings: {
        receiveNotifications: true,
        darkMode: false,
        language: 'English',
        emailPreferences: {
          promotions: true,
          newsUpdates: true,
          productRecommendations: true,
          // Add more email preferences here
        },
      },
      shippingAddresses: [
        {
          id: 1,
          address: '123 Main St, City, Country',
        },
        {
          id: 2,
          address: '456 Elm St, City, Country',
        },
      ],
      newAddress: '',
      changePassword: {
        currentPassword: '',
        newPassword: '',
        confirmPassword: '',
      },
      isChangePasswordVisible: false,
      paymentInfo: [], // Initialize with an empty array or preloaded payment info
      newPaymentInfo: {
        cardType: '',
        lastFourDigits: '',
      },
      isShippingAddressesOpen: false,
      isPaymentDetailsOpen: false,
    };
  }

  // ... (Same methods as in the previous code)

  render() {
    return (
      <div className="account-container">
        <header>
          <h1>
            <img src={logo} alt="" width="150px" height="100px" />
            <i>Panda Shop</i>
          </h1>
        </header>

        <div className="main-content">
          <div className="left-column">
            <div className="user-profile">
              <h2>User Profile</h2>
              <p>Name: {this.state.user.name}</p>
              <p>Email: {this.state.user.email}</p>
              <p>Address: {this.state.user.address}</p>
              <p>Phone Number: {this.state.user.phoneNumber}</p>
              {/* Add more user information fields */}
            </div>

            <div className="settings-icon">
              <Dropdown>
                {/* ... Dropdown settings for user settings */}
              </Dropdown>
            </div>

            <div className="change-password-section">
              <Dropdown>
                {/* ... Dropdown settings for changing password */}
              </Dropdown>
            </div>
          </div>

          <div className="right-column">
            <div className="shipping-address">
              <h2 onClick={this.toggleShippingAddresses}>
                Shipping Addresses{" "}
                <i
                  className={`fa ${
                    this.state.isShippingAddressesOpen
                      ? 'fa-chevron-up'
                      : 'fa-chevron-down'
                  }`}
                ></i>
              </h2>
              {this.state.isShippingAddressesOpen && (
                <ul>
                  {this.state.shippingAddresses.map((address) => (
                    <li key={address.id}>
                      {address.address}
                      <button
                        onClick={() => this.deleteShippingAddress(address.id)}
                      >
                        Delete
                      </button>
                    </li>
                  ))}
                </ul>
              )}
              <div>
                <input
                  type="text"
                  placeholder="Enter a new address"
                  value={this.state.newAddress}
                  onChange={this.handleNewAddressChange}
                />
                <button onClick={this.addShippingAddress}>Add Address</button>
              </div>
            </div>

            <div className="payment-info">
              <h2 onClick={this.togglePaymentDetails}>
                Payment Information{" "}
                <i
                  className={`fa ${
                    this.state.isPaymentDetailsOpen
                      ? 'fa-chevron-up'
                      : 'fa-chevron-down'
                  }`}
                ></i>
              </h2>
              {this.state.isPaymentDetailsOpen && (
                <div>
                  {this.state.paymentInfo.map((payment, index) => (
                    <div key={index}>
                      <p>Card Type: {payment.cardType}</p>
                      <p>Last 4 Digits: {payment.lastFourDigits}</p>
                      <button
                        onClick={() => this.deletePaymentInfo(index)}
                      >
                        Delete
                      </button>
                    </div>
                  ))}
                </div>
              )}
              <div>
                <input
                  type="text"
                  name="cardType"
                  placeholder="Card Type"
                  value={this.state.newPaymentInfo.cardType}
                  onChange={this.handlePaymentInputChange}
                />
                <input
                  type="text"
                  name="lastFourDigits"
                  placeholder="Last 4 Digits"
                  value={this.state.newPaymentInfo.lastFourDigits}
                  onChange={this.handlePaymentInputChange}
                />
                <button onClick={this.addPaymentInfo}>Add Payment Info</button>
              </div>
            </div>
          </div>
        </div>

        <footer>
          <p>Contact us at support@example.com</p>
          <p>&copy; 2023 Your E-Commerce App</p>
        </footer>
      </div>
    );
  }
}

export default Account;
