import React, { Component } from 'react';

class AccountSection extends Component {
  constructor(props) {
    super(props);
    this.state = {
      user: {
        name: 'John Doe',
        email: 'johndoe@example.com',
        // Other user data
      },
      settings: {
        receiveNotifications: true,
        darkMode: false,
        // Add other user settings
      },
    };
  }

  // Methods for handling user settings
  handleNotificationToggle = () => {
    this.setState((prevState) => ({
      settings: {
        ...prevState.settings,
        receiveNotifications: !prevState.settings.receiveNotifications,
      },
    }));
  };

  handleDarkModeToggle = () => {
    this.setState((prevState) => ({
      settings: {
        ...prevState.settings,
        darkMode: !prevState.settings.darkMode,
      },
    }));
  };

  render() {
    return (
      <div>
        <h1>Account Information</h1>
        <div>
          <h2>User Profile</h2>
          <p>Name: {this.state.user.name}</p>
          <p>Email: {this.state.user.email}</p>
          {/* Add forms and UI elements for editing user information */}
        </div>
        <div>
          <h2>User Settings</h2>
          <label>
            Receive Notifications:
            <input
              type="checkbox"
              checked={this.state.settings.receiveNotifications}
              onChange={this.handleNotificationToggle}
            />
          </label>
          <br />
          <label>
            Dark Mode:
            <input
              type="checkbox"
              checked={this.state.settings.darkMode}
              onChange={this.handleDarkModeToggle}
            />
          </label>
          {/* Add more user settings options */}
        </div>
        {/* Add other sections like order history, wish lists, etc. */}
      </div>
    );
  }
}

export default AccountSection;
