import React, { useState } from "react";
import { ToastContainer } from "react-toastify";
import axios from "axios";
import add from './add.jpg';
import view from "./view.jpg";
import logo from './logo.png';

const Services = () => {
  const [inputValue, setInputValue] = useState({
    item_name: "",
    item_type: "",
    description: "",
    price: "",
    size: "",
    manufacturer: "",
    item_image: "",
  });
  const { item_name, item_type, description, price, size, manufacturer, item_image } = inputValue;

  const handleOnChange = (e) => {
    const { id, value } = e.target;
    setInputValue({
      ...inputValue,
      [id]: value,
    });
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setInputValue({
          ...inputValue,
          item_image: reader.result,
        });
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const { data } = await axios.post(
        "http://localhost:4000/additem",
        {
          ...inputValue,
        },
        { withCredentials: true }
      );
      const { success, message } = data;
      if (success) {
        alert("Item Added Successfully!!!");
      } else {
        alert(message);
      }
    } catch (error) {
      console.log(error);
    }
    setInputValue({
      item_name: "",
      item_type: "",
      description: "",
      price: "",
      size: "",
      manufacturer: "",
      item_image: "",
    });
  };

  return (
    <>
      <header>
        <h1>
          <img src={logo} alt="" width="150px" height="100px" />
          <i>Panda Shop</i>
        </h1>
      </header>
      <nav>
        <a href="/">Home</a>
        <a href="/shop">Shop</a>
        <a href="/categories">Categories</a>
        <a href="#">Account</a>
        <div className="search-bar">
          <input type="text" placeholder="Search products..." />
          <button type="submit">Search</button>
        </div>
        <a href="/Services">Services</a>
        <a href="/aboutUs">About Us</a>
        <a href="#">Chatbot</a>
        <a className="button" href="/login">
          Logout
        </a>
      </nav>

      <br />
      <center>
        <h1>Welcome</h1>
        <p>You can sell your products here!</p>
      </center>
      <div class="container-fluid">
        <div class="row">
          <div class="col-sm-12 col-md-4 col-lg-2"></div>
          <div class="col-sm-12 col-md-4 col-lg-3">
            <div class="card">
              <img src={add} alt="" width="auto" height="250px" />
              <div class="card-body">
                <h5 class="card-title">Add Item</h5>
                <button
                  type="button"
                  class="btn btn-success"
                  data-bs-toggle="modal"
                  data-bs-target="#exampleModal"
                >
                  Add
                </button>
              </div>
            </div>
          </div>
          <div class="col-sm-12 col-md-4 col-lg-2"></div>
          <div class="col-sm-12 col-md-4 col-lg-3">
            <div class="card">
              <img src={view} alt="" width="auto" height="250px" />
              <div class="card-body">
                <h5 class="card-title">View Item</h5>
                <a
                  href="/view"
                  class="btn btn-success"
                  style={{ backgroundColor: "#FFA500" }}
                >
                  View
                </a>
              </div>
            </div>
          </div>
          <div class="col-sm-12 col-md-4 col-lg-2"></div>
        </div>
      </div>

      <div class="modal fade" id="exampleModal" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
        <div class="modal-dialog">
          <div class="modal-content">
            <div class="modal-header">
              <h1 class="modal-title fs-5" id="exampleModalLabel">
                Add Items to the App
              </h1>
              <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
            </div>
            <div class="modal-body">
              <div>
                <form onSubmit={handleSubmit}>
                  <div class="mb-3">
                    <label for="item_name" class="form-label">
                      Item Name
                    </label>
                    <input
                      type="text"
                      className="form-control"
                      id="item_name"
                      placeholder="Enter item name"
                      value={item_name}
                      onChange={handleOnChange}
                      required
                    />
                  </div>
                  <div class="mb-3">
                    <label for="item_type" class="form-label">
                      Item type
                    </label>
                    <input
                      type="text"
                      className="form-control"
                      id="item_type"
                      placeholder="Enter item type"
                      value={item_type}
                      onChange={handleOnChange}
                      required
                    />
                  </div>
                  <div class="mb-3">
                    <label for="description" class="form-label">
                      Description:
                    </label>
                    <input
                      type="text"
                      className="form-control"
                      id="description"
                      placeholder="Enter description"
                      value={description}
                      onChange={handleOnChange}
                      required
                    />
                  </div>
                  <div class="mb-3">
                    <label for="price" class="form-label">
                      Price
                    </label>
                    <input
                      type="text"
                      className="form-control"
                      id="price"
                      placeholder="Enter the price"
                      value={price}
                      onChange={handleOnChange}
                      required
                    />
                  </div>
                  <div class="mb-3">
                    <label for="size" class="form-label">
                      Size
                    </label>
                    <input
                      type="text"
                      className="form-control"
                      id="size"
                      placeholder="Enter the size"
                      value={size}
                      onChange={handleOnChange}
                      required
                    />
                  </div>
                  <div class="mb-3">
                    <label for="manufacturer" class="form-label">
                      Manufacturer
                    </label>
                    <input
                      type="text"
                      className="form-control"
                      id="manufacturer"
                      placeholder="Enter the manufacturer"
                      value={manufacturer}
                      onChange={handleOnChange}
                      required
                    />
                  </div>
                  <div class="mb-3">
                    <label for="item_image" class="form-label">
                      Image
                    </label>
                    <input
                      type="file"
                      class="form-control"
                      id="item_image"
                      placeholder=".jpeg file"
                      onChange={handleImageChange}
                      accept="image/*"
                    />
                  </div>
                  <div class="modal-footer">
                    <button type="button" class="btn" data-bs-dismiss="modal">
                      Close
                    </button>
                    <button type="submit" class="btn">
                      Add Item
                    </button>
                  </div>
                </form>
              </div>
            </div>
          </div>
        </div>
      </div>

      <ToastContainer />
    </>
  );
};

export default Services;
